package java_lessons;

public class Demo_info {
static int a=90;
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("A="+ a);
		rinfo();
		System.out.println("A="+ a);
		ginfo();

	}
	public static void rinfo()
	{
		int a=20;
		System.out.println("A="+ a);
	}
	public static void ginfo()
	{
		System.out.println("A="+ a);
	}
}
